import React, { useContext, useState } from 'react';
import '../App.css';
import { TransactionContext } from './transContext';


function Header() {

    let { transactions, addTransaction } = useContext(TransactionContext);
    let [newDesc, setDesc] = useState();
    let [newAmount, setAmount] = useState();



    const handleAddition = (event) => {

        console.log(newDesc, newAmount)
        event.preventDefault()
        addTransaction({
            amount: newAmount,
            desc: newDesc
        })

    }
    const getIncome = () => {
        let income = 0;
        for (var i = 0; i < transactions.length; i++) {
            if (transactions[i].amount > 0)
                income = income + transactions[i].amount
        }
        return income;
    }
    const getExpense = () => {
        let expense = 0;
        for (var i = 0; i < transactions.length; i++) {
            if (transactions[i].amount < 0)
                expense  =  expense + transactions[i].amount
        }
        return expense;
    }

        return (
            <div className='header'>
                <h1 className='textcenter'>Expense Traker </h1>
                <h3>Your Balance  <br />500</h3>


                <div className='expense-container'>

                    <h3 className='income'>INCOME<br /> {getIncome()}</h3>
                    <h3>EXPENSE <br /> {getExpense()}</h3>



                </div>
                <h3>History</h3>
                <hr></hr>
                <ul className='transaction-list'>
                    {transactions.map((transObj, ind) => {
                        return (

                            <li key={ind}><span>{transObj.amount}</span>
                                <span>{transObj.desc}</span>
                            </li>

                        )
                    })}


                </ul>


                <h3>Add Transaction</h3>
                <hr />



                <form className='transaction-form' onSubmit={handleAddition}>
                    <label>
                        Enter Discription
                        <br />
                        <input type='text' onChange={(ev) => setDesc(ev.target.value)} required />



                    </label>
                    <br ></br>
                    <label>
                        Enter Amount
                        <br />
                        <input type='number' onChange={(ev) => setAmount(ev.target.value)} required />



                    </label>
                    <br />

                    <input type='submit' value='Add Transaction' />
                </form>
            </div>
        )
    }
    export default Header;
