import React, { useContext, useState } from 'react';
import '../App.css';
import { TransactionContext } from './transContext';
import { TransactionHistory } from './transHistory';
import Head from './header1.js';


function Header() {

    let { transactions, addTransaction } = useContext(TransactionContext);
    let [newDesc, setDesc] = useState("");
    let [newAmount, setAmount] = useState();



    const handleAddition = (event) => {


        event.preventDefault()
        if (newAmount === 0){
            alert("Please use correct Value")
        }
        addTransaction({
            amount: Number(newAmount),
            desc: newDesc
        })
        setDesc('');
        setAmount('0');

    }
    const getIncome = () => {
        let income = 0;


        for (var i = 0; i < transactions.length; i++) {
            if (transactions[i].amount > 0)
                income = income + transactions[i].amount

        }
        return income;
    }
    const getExpense = () => {

        let expense = 0;
        for (var i = 0; i < transactions.length; i++) {
            if (transactions[i].amount < 0)
                expense  =  expense +  transactions[i].amount

        }
        return expense;


    }

        return (
            <div className='header'>
                {/* <h1 className='textcenter'>Expense Traker </h1> */}
                <Head />
                <div className='balance-container'>
                    <h3 className='heading textcenter'>BALANCE  <br />${getIncome()+ getExpense()}</h3>
</div>

                <div className='expense-container'>

                    <h3 className={'heading income-color '}>INCOME
                        <h3 className="income-value">${getIncome()}  </h3>
                     </h3>
                    <hr className= "line"/>
                    <h3 className={'heading expense-color'}>EXPENSE 
                    <h3 className='expense-value'> ${getExpense()} </h3>
                    </h3>



                </div>



                <TransactionHistory />


                <h3 className="history">Add Transaction</h3>
                <hr className='hr' />



                <form className='transaction-form' onSubmit={handleAddition}>
                    <label>
                        Enter Discription

                        <input type='text' onChange={(ev) => setDesc(ev.target.value)} required value={newDesc} placeholder='Description'/>



                    </label>

                    <label className='cash'>
                        Enter Amount
                        <br />
                        <input type='number' onChange={(ev) => setAmount(ev.target.value)} required value={newAmount} placeholder='00'/>



                    </label>
                    <br />

                    <input type='submit' value='Add Transaction' className='submit'/>
                </form>
            </div>
        )
    }
    export default Header;
