import { React, createContext, useReducer } from 'react';
import TransactionReducer from './transReducer';
const initialTrans = [
    { id: 0, amount: 400, desc: "Cash" },
    { id: 1, amount: -50, desc: "book" },
    { id: 2, amount: -200, desc: "Cash" },
    { id: 3, amount: 600, desc: "salary" }
]


export const TransactionContext = createContext(initialTrans);



export const TransactionProvider = ({ children }) => {
    let [state, dispatch] = useReducer(TransactionReducer, initialTrans)

    function addTransaction(TransactionContext) {
        dispatch({
            type: "ADD",
            payload: {
                TransactionContext
            },
        })

    }
    function delTransaction(id) {
        dispatch({
            type: "DELETE_TRANSACTION",
            payload: id
        });
    }

    return (
        <TransactionContext.Provider value={{
            transactions: state,
            addTransaction,
            delTransaction
        }}>
            {children}
        </TransactionContext.Provider>


    )
}
